 import java.awt.*;import java.awt.event.*;import atm.ATM;import simulation.Simulation; public class ATMMain{    public static void main(String[] args)    {        ATM theATM = new ATM(42, "Gordon College", "First National Bank of Podunk",                             null );        Simulation theSimulation = new Simulation(theATM);                               Frame mainFrame = new Frame("ATM Simulation");        mainFrame.add(theSimulation.getGUI());                              MenuBar menuBar = new MenuBar();        Menu fileMenu = new Menu("File");        MenuItem quitItem = new MenuItem("Quit", new MenuShortcut('Q'));        MenuItem changeDayMode = new MenuItem("Day Mode", new MenuShortcut('D'));        MenuItem changeNightMode = new MenuItem("Night Mode", new MenuShortcut('N'));        quitItem.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e)            {                System.exit(0);            }        });        changeNightMode.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e)            {               theSimulation.getGUI().changeNightMode();            }        });        changeDayMode.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e)            {               theSimulation.getGUI().changeDayMode();            }        });        fileMenu.add(changeNightMode);        fileMenu.add(changeDayMode);        fileMenu.add(quitItem);        menuBar.add(fileMenu);        mainFrame.setMenuBar(menuBar);        mainFrame.addWindowListener(new WindowAdapter() {            public void windowClosing(WindowEvent e)            {                System.exit(0);            }        });                       new Thread(theATM).start();                                        mainFrame.setResizable(false);        mainFrame.pack();        mainFrame.setVisible(true);    }}    