 package atm.physical;import simulation.Simulation; public class EnvelopeAcceptor{        public EnvelopeAcceptor(Log log)    {        this.log = log;    }           public void acceptEnvelope() throws CustomerConsole.Cancelled    {        boolean inserted = Simulation.getInstance().acceptEnvelope();        if (inserted)            log.logEnvelopeAccepted();        else            throw new CustomerConsole.Cancelled();    }            private Log log;}