 package atm.physical;import java.util.Enumeration;import banking.Balances;import banking.Receipt;import simulation.Simulation; public class ReceiptPrinter{        public ReceiptPrinter()    {     }           public void printReceipt(Receipt receipt)    {        Enumeration receiptLines = receipt.getLines();                        while (receiptLines.hasMoreElements())        {            Simulation.getInstance().printReceiptLine(                ((String) receiptLines.nextElement()));        }    }}