 package atm.transaction;import atm.ATM;import atm.Session;import atm.physical.*;import banking.AccountInformation;import banking.Card;import banking.Message;import banking.Money;import banking.Status;import banking.Receipt;public class Withdrawal extends Transaction{        public Withdrawal(ATM atm, Session session, Card card, int pin)    {        super(atm, session, card, pin);    }            protected Message getSpecificsFromCustomer() throws CustomerConsole.Cancelled    {    	        from = atm.getCustomerConsole().readMenuChoice(            "Account to withdraw from",            AccountInformation.ACCOUNT_NAMES);                String [] amountOptions = { "$20", "$40", "$60", "$100", "$200" };        Money [] amountValues = {                                   new Money(20), new Money(40), new Money(60),                                  new Money(100), new Money(200)                                };                                          String amountMessage = "";        boolean validAmount = false;                while (! validAmount)        {            amount = amountValues [                 atm.getCustomerConsole().readMenuChoice(                    amountMessage + "Amount of cash to withdraw", amountOptions) ];                                        validAmount = atm.getCashDispenser().checkCashOnHand(amount);            if (! validAmount)                amountMessage = "Insufficient cash available\n";        }               return new Message(Message.WITHDRAWAL,                            card, pin, serialNumber, from, -1, amount);    }           protected Receipt completeTransaction()    {        atm.getCashDispenser().dispenseCash(amount);        return new Receipt(this.atm, this.card, this, this.balances) {            {                detailsPortion = new String[2];                detailsPortion[0] = "WITHDRAWAL FROM: " +                                     AccountInformation.ACCOUNT_ABBREVIATIONS[from];                detailsPortion[1] = "AMOUNT: " + amount.toString();            }        };    }           private int from;            private Money amount;           }