 package simulation;import banking.Money;import java.awt.*;import java.awt.event.*;class BillsPanel extends Panel{        BillsPanel()    {        setLayout(new GridLayout(0, 1, 0, 0));        setFont(new Font("Monospaced", Font.PLAIN, 14));        labels = new Label[6];        labels[0] = new Label("A real ATM would have a mechanism to sense",                Label.CENTER);        labels[1] = new Label("or allow the operator to enter the number",                Label.CENTER);        labels[2] = new Label("of $20 bills in the cash dispenser.",                Label.CENTER);        labels[3] = new Label("For purposes of the simulation,",                Label.CENTER);        labels[4] = new Label("please enter the number of $20 bills manually.",                Label.CENTER);        labels[5] = new Label("Then press RETURN",                Label.CENTER);                for(int i = 0;i<labels.length;i++)        	add(labels[i]);                billsNumberField = new TextField(30);        billsNumberField.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e)            {                synchronized(BillsPanel.this)                {                    BillsPanel.this.notify();                }            }        });        billsNumberPanel = new Panel();        billsNumberPanel.add(billsNumberField);        add(billsNumberPanel);      }      synchronized int readBills()    {        boolean validNumberRead = false;        int billsNumber = 0;                billsNumberField.setText("");                while(! validNumberRead)        {            billsNumberField.requestFocus();            try            {                wait();            }            catch(InterruptedException e)            { }                                try            {                billsNumber = Integer.parseInt(billsNumberField.getText());                if (billsNumber >= 0)                    validNumberRead = true;                else                    getToolkit().beep();            }            catch(NumberFormatException e)            {                getToolkit().beep();            }            if (! validNumberRead)            {                billsNumberField.setText("Must be a valid integer >= 0");                billsNumberField.selectAll();            }        }        return billsNumber;    }        public void changeNightMode()    {    	this.setBackground(Color.black);    	for(int i = 0;i<labels.length;i++)    	{    		labels[i].setBackground(Color.black);    		labels[i].setForeground(Color.white);    	}    	billsNumberField.setBackground(new Color(127,127,127));    	billsNumberField.setForeground(Color.white);    	billsNumberPanel.setBackground(Color.black);     }        public void changeDayMode()    {    	this.setBackground(Color.white);    	for(int i = 0;i<labels.length;i++)    	{    		labels[i].setBackground(Color.white);    		labels[i].setForeground(Color.black);    	}    	billsNumberField.setBackground(Color.white);    	billsNumberField.setForeground(Color.black);    	billsNumberPanel.setBackground(Color.white);     }       private TextField billsNumberField;    private Label[] labels;    private Panel billsNumberPanel;}