package simulation;import java.awt.*;import java.awt.event.*;class CardPanel extends Panel{       CardPanel()    {        setLayout(new GridLayout(0, 1, 0, 0));        setFont(new Font("Monospaced", Font.PLAIN, 14));        labels = new Label[8];        labels[0] = new Label("A real ATM would have a magnetic",                Label.CENTER);        labels[1] = new Label("stripe reader to read the card",                Label.CENTER);        labels[2] = new Label("For purposes of the simulation,",                Label.CENTER);        labels[3] = new Label("please enter the card number manually.",                Label.CENTER);        labels[4] = new Label("Then press RETURN",                Label.CENTER);        labels[5] = new Label("(An invalid integer or an integer not",                Label.CENTER);        labels[6] = new Label("greater than zero will be treated as",                Label.CENTER);        labels[7] = new Label("an unreadable card)",                Label.CENTER);        for(int i = 0;i<labels.length;i++)        	add(labels[i]);                cardNumberField = new TextField(30);        cardNumberField.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e)            {                synchronized(CardPanel.this)                {                    CardPanel.this.notify();                }            }        });        Panel cardNumberPanel = new Panel();        cardNumberPanel.add(cardNumberField);        add(cardNumberPanel);    }           synchronized int readCardNumber()    {        cardNumberField.setText("");                cardNumberField.requestFocus();        try        {            wait();        }        catch(InterruptedException e)        { }                        int cardNumber;        try        {            cardNumber = Integer.parseInt(cardNumberField.getText());            if (cardNumber <= 0)                cardNumber = -1;        }        catch(NumberFormatException e)        {            cardNumber = -1;        }                return cardNumber;    }    public void changeNightMode()    {    	this.setBackground(Color.black);    	for(int i = 0;i<labels.length;i++)    	{    		labels[i].setBackground(Color.black);    		labels[i].setForeground(Color.white);    	}    	cardNumberField.setBackground(new Color(127,127,127));    	cardNumberField.setForeground(Color.white);    	cardNumberPanel.setBackground(Color.black);     }        public void changeDayMode()    {    	this.setBackground(Color.white);    	for(int i = 0;i<labels.length;i++)    	{    		labels[i].setBackground(Color.white);    		labels[i].setForeground(Color.black);    	}    	cardNumberField.setBackground(Color.white);    	cardNumberField.setForeground(Color.black);    	cardNumberPanel.setBackground(Color.white);     }        private TextField cardNumberField;    private Label[] labels;    private Panel cardNumberPanel;}