 package simulation;import atm.ATM;import banking.Card;import java.awt.*;import java.awt.event.*;class SimCardReader extends Button{        SimCardReader(final Simulation simulation)    {        super("Click to insert card");        addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e)            {                simulation.cardInserted();            }        });                       changeDayMode();        setVisible(false);    }           void animateInsertion()    {        originalBounds = getBounds();        Rectangle currentBounds =           new Rectangle(originalBounds.x, originalBounds.y,                         originalBounds.width, originalBounds.height);                                 while (currentBounds.width > 0 && currentBounds.height > 0)        {             setBounds(currentBounds.x, currentBounds.y,                      currentBounds.width, currentBounds.height);            repaint();            try             {                 Thread.sleep(100);            }             catch (InterruptedException e)             { }                        currentBounds.height -= 1;            currentBounds.width =               (originalBounds.width * currentBounds.height) / originalBounds.height;            currentBounds.x =              originalBounds.x + (originalBounds.width - currentBounds.width) / 2;            currentBounds.y =              originalBounds.y + (originalBounds.height - currentBounds.height) / 2;        }                  setVisible(false);      }            void animateEjection()    {        setLabel("Ejecting card");        setVisible(true);                Rectangle currentBounds =            new Rectangle(originalBounds.x + originalBounds.width / 2,                         originalBounds.y + originalBounds.height / 2,                         originalBounds.width / originalBounds.height, 1);                while (currentBounds.height <= originalBounds.height &&               currentBounds.width <= originalBounds.width)        {             setBounds(currentBounds.x, currentBounds.y,                      currentBounds.width, currentBounds.height);            repaint();            try             {                 Thread.sleep(100);            }             catch (InterruptedException e)             { }            currentBounds.height += 1;            currentBounds.width =               (originalBounds.width * currentBounds.height) / originalBounds.height;            currentBounds.x =              originalBounds.x + (originalBounds.width - currentBounds.width) / 2;            currentBounds.y =              originalBounds.y + (originalBounds.height - currentBounds.height) / 2;        }                  setLabel("Click to insert card");    }       void animateRetention()    {        setLabel("Click to insert card");        setVisible(true);    }        public void changeNightMode()    {    	this.setBackground(Color.black);    	this.setForeground(Color.white);    }        public void changeDayMode()    {    	this.setBackground(Color.white);    	this.setForeground(Color.black);   	    }        private Rectangle originalBounds;}                                       