 package simulation;import java.awt.*;import java.awt.event.*;class SimReceiptPrinter extends Panel{       SimReceiptPrinter()    {        setLayout(new BorderLayout(5, 5));                              printArea =             new TextArea("", ATMPanel.PRINTABLE_LINES, ATMPanel.PRINTABLE_CHARS,                         TextArea.SCROLLBARS_VERTICAL_ONLY);        printArea.setBackground(new Color(210,210,210));        printArea.setForeground(Color.black);        printArea.setFont(new Font("Monospaced", Font.PLAIN, 12));        printArea.setEditable(false);        add(printArea, BorderLayout.SOUTH);                                    Panel buttonPanel = new Panel();        buttonPanel.setLayout(new GridLayout(1,1));        take = new Button("Take receipt");        buttonPanel.add(take);        add(buttonPanel, BorderLayout.NORTH);        take.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e)            {                printArea.setText("");                take.setVisible(false);            }        });        this.buttonPanel = buttonPanel;        changeDayMode();        take.setVisible(false);    }            void println(String text)    {        printArea.append(text + '\n');        try        {             Thread.sleep(1 * 1000);        }        catch (InterruptedException e)        { }       take.setVisible(true);    }                public void changeNightMode()    {    	this.setBackground(Color.black);    	take.setBackground(Color.black);    	take.setForeground(Color.white);    	buttonPanel.setBackground(Color.black);    	printArea.setBackground(Color.black);    	printArea.setForeground(Color.white);    }       public void changeDayMode()    {    	this.setBackground(new Color(242,242,242));    	take.setBackground(new Color(242,242,242));    	take.setForeground(Color.black);    	buttonPanel.setBackground(new Color(242,242,242));    	printArea.setBackground(new Color(210,210,210));    	printArea.setForeground(Color.black);    }    private TextArea printArea;        private Panel buttonPanel;        private Button take;}                                       